const concepto = $input.item.json.concepto;

if (!concepto || concepto.trim() === '') {
  throw new Error('Concepto vacío');
}

return {
  concepto: concepto.trim().toUpperCase(),
  timestamp: new Date().toISOString(),
  session_id: `session_${Date.now()}`
};

