parameters.jsCode:
const apiResult = $input.first().json;
const geminiResult = $input.last().json;

// Extraer texto de Gemini
let analisisGemini = {};
try {
  const textoGemini = geminiResult.candidates[0].content.parts[0].text;
  // Limpiar markdown si existe
  let jsonStr = textoGemini.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  analisisGemini = JSON.parse(jsonStr);
} catch (e) {
  analisisGemini = { error: 'No se pudo parsear respuesta de Gemini', raw: geminiResult };
}

return {
  concepto: apiResult.concepto,
  es_maximo_relacional: apiResult.es_maximo_relacional,
  certeza_combinada: apiResult.certeza_combinada,
  similitud_promedio: apiResult.similitud_promedio,
  rutas: apiResult.rutas,
  gemini_analisis: analisisGemini,
  neo4j_guardado: true,
  timestamp: new Date().toISOString()
};
