const datosOriginales = $input.first().json;
const geminiResult = $input.last().json;

// Extraer análisis de Gemini
let analisisGemini = {};
try {
  const textoGemini = geminiResult.candidates[0].content.parts[0].text;
  let jsonStr = textoGemini.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  analisisGemini = JSON.parse(jsonStr);
} catch (e) {
  analisisGemini = { 
    error: 'No se pudo parsear respuesta de Gemini', 
    raw_text: geminiResult.candidates?.[0]?.content?.parts?.[0]?.text || 'No disponible'
  };
}

return {
  concepto: datosOriginales.concepto,
  es_maximo_relacional: datosOriginales.es_maximo_relacional,
  certeza_combinada: datosOriginales.certeza_combinada,
  similitud_promedio: datosOriginales.similitud_promedio,
  rutas: datosOriginales.rutas,
  gemini_analisis: analisisGemini,
  neo4j_guardado: true,
  sistema: "YO Estructural v1.0",
  timestamp: new Date().toISOString()
};
