  {
    "tipo": "etimologica",
    "ruta": ["SOPORTE", "supportare", "sub-portare", "llevar desde abajo"],
    "certeza": 0.95,
    "similitud": 0.92
  },
  {
    "tipo": "sinonímica",
    "ruta": ["SOPORTE", "apoyo", "sustento", "base"],
    "certeza": 0.88,
    "similitud": 0.85
  },
  {
    "tipo": "antonímica",
    "ruta": ["SOPORTE", "abandonar", "desamparo", "ausencia"],
    "certeza": 0.82,
    "similitud": 0.78
  },
